import axios from 'axios';
class Search extends Component {
    constructor(props) {
        super(props)
               this.state = {
                persons: []
              }
            }   
        componentDidMount() {
          axios.get('http://localhost:8080/api/getStudent').then(res => {
              const persons = res.data
              this.setState({ persons })
            })
        }  
        handleSubmit(e)
        {
            console.log("in handle>>>>>>>",e.target.value);
            axios.delete('http://localhost:8080apiapi/deleteStudent/{e.target.value}')
            .then(res => {
                const persons=res.data
                this.setState({persons})
            })
        }
        render() {
          return (
          <div className="App">
             <div className="left">
             <table border="2">
              <tr>
                <th>id</th>
                <th>firstname</th>
                <th>username</th>
                <th>password</th>
                <th>edit</th>
                <th>delete</th>
              </tr>
              { this.state.persons.map(person => 
                <tr>
                  <td>{person.id}</td>
                  <td>{person.firstname}</td>
                  <td>{person.username}</td>
                  <td>{person.password}</td>
                  <td>
                    <form onClick={this.handleEditSubmit}>
                        <button value={person.id}>Edit</button>
                    </form>

                    </td>
                    <td>  
{/*                    <form onClick={this.handleSubmit}>

                    </form> */}
                    <button  onClick={this.handleSubmit} value={person.id}>Delete</button>
                  </td>
                  </tr>
                )}
              </table>
            </div>    </div>
        ) }

}
export default Search
