import React,{ Component } from 'react';
import axios from 'axios';

class Form1 extends Component
{
    constructor(props)
    {
        super(props)
        this.handleSubmit=this.handleSubmit.bind(this)
        this.handleChange=this.handleChange.bind(this)
        this.state={
              employeeForm:{
                  firstname:'',
                  username:'',
                  password:''
              }
        }
        console.log(this.state.employeeForm)
    }
   componentDidMount()
    {
        let params = (new URL(document.location)).searchParams;
let id = params.get("id");
         console.log("in edit>>>>>",id);
             axios.get('http://localhost:8080/api/getStudentById/'+id).then(res=>{
                const persons=res.data
              this.setState({employeeForm:persons})
              console.log("delted : ", persons);
            })
    }

    handleSubmit()
    {
      // var data={firstname:this.state.firstname,username:this.state.username,password:this.state.password}
        let formdata = new FormData();
        formdata.append("firstname",this.state.firstname)
        formdata.append("username",this.state.firstname)
        formdata.append("password",this.state.firstname)

      axios.post('http://localhost:8080/api/insertstudent', formdata);
      console.log("helo",formdata);
    //   window.target.href="/"

    }
    handleChange(e)
    {   
        console.log(" data ",e.target.name," ",e.target.value)
        this.setState({
            [e.target.name]:e.target.value
        })
    }


    render(){
        return(
            <div>
             <form>
                 <label> firstname
                 <input type="text" name="firstname"  value={this.state.firstname}
                               onChange={this.handleChange} id="firstName"/></label>
                   <br/><br/>   
                   <label> username
                 <input type="text" name="username" 
                               onChange={this.handleChange} id="username"/></label>
                    <br/><br/> 
                    <label> password
                 <input type="text" name="password" 
                               onChange={this.handleChange} id="password"/></label>

                    <br/><br/>



                    <button onClick={this.handleSubmit}>submit</button>     

                 </form>
                 <a href="/search">Search</a>

            </div>
        )
    }

}
export default Form1
