import React,{ Component } from 'react';
import axios from 'axios';
class Search extends Component {
    constructor(props) {
        super(props)
               this.state = {
                persons: []
              }
            }   
        componentDidMount() {
          axios.get('http://localhost:8080/api/getStudent').then(res => {
              const persons = res.data
              this.setState({ persons })
            })
        }  
        handleSubmit()
        {
            axios.put('http://localhost:8080api/deleteStudent/{person.id}')
            .then(res => {
                const persons=res.data
                this.setState({persons})
            })
        }
        render() {
          return (
          <div className="App">
             <div className="left">
             <table border="2">
              <tr>
                <th>id</th>
                <th>firstname</th>
                <th>username</th>
                <th>password</th>
                <th>edit</th>
                <th>delete</th>
              </tr>
              { this.state.persons.map(person => 
                <tr>
                  <td>{person.id}</td>
                  <td>{person.firstname}</td>
                  <td>{person.username}</td>
                  <td>{person.password}</td>
                  <td>
                    <form onSubmit={this.handleEditSubmit}>
                        <button type="submit" value={person.id}>Edit</button>
                    </form>

                    </td>
<td>                     <form onSubmit={this.handleSubmit}>
                        <button type="submit" value={person.id} onClick={e => this.onClick(e)}>Delete</button>
                    </form>

                  </td>
                  </tr>
                )}
              </table>
            </div>    </div>
        ) }

}
export default Search
